html.page-home {
	position: absolute;
	left: 0;
	right: 0;
	top: 0;
	bottom: 0;
	/*background: linear-gradient( #1abc9c, transparent), linear-gradient( 90deg, skyblue, transparent), linear-gradient( -90deg, coral, transparent);*/
	/*background-blend-mode: screen;*/
	/*background: linear-gradient(to left, #5f2c82, #49a09d);*/
	background: #000;
	background-color: transparent;
	background-image: url("/images/bg.jpg");
	background-repeat: no-repeat;
	background-position: center center;
	background-size: cover;
}


.qrcode_container {
	position: fixed;
	right: 999px;
	top: 140px;
	padding: 10px 0 0;
	width: 140px;
	background-color: #fff;
	box-shadow: -1px 0 2px .5px rgba(0,127,255,.2);
	text-align: center;
}

.ad_container {
	position: fixed;
	right: -999px;
	top: 360px;
	padding: 10px 0 0;
	width: 140px;
	background-color: #fff;
	text-align: center;
}

.ad_container img {
	height: 150px;
}

@media (min-width: 480px) and (orientation: landscape) {
	.qrcode_container {
		right: 0 !important;
	}
	.ad_container {
		right: 0 !important;
	}
}


.qrcode_container > div h4 {
	margin: 0;
	font-size: 15px;
	color: #007fff;
}

.qrcode_container > div p {
	margin: 10px 0;
	font-size: 12px;
}

.qrcode_container > div img {
	width: 140px;
}
